import React from 'react';
import '../App.css';
import { Button } from './Button';
import './HeroSection.css';

function DiffColor () {
  const str=""
  const str1=arguments[0]+arguments[1]
  const str2=arguments[2]
  const str3=arguments[3]=arguments[4]

  const color1="blue"
  const color2="white"
  const color3="red"

  let i=1; i<3; i++
  str=str.concat(`str[i]`.fontcolor(`color[i]`))
}


function HeroSection() {
  return (
    <div className='hero-container'>
      <h1>{DiffColor("法国大革命")}</h1>
      <p>了解波澜壮阔的革命历史</p>
      <div className='hero-btns'>
        <Button
          className='btns'
          buttonStyle='btn--outline'
          buttonSize='btn--large'
        >
          开始历史之旅
        </Button>
      </div>
    </div>
  );
}

export default HeroSection;
